import { useState } from "react";

// Centralizes modal control
const useDone = () => {
  const [isDone, setIsDone] = useState(false);

  const close2 = () => setIsDone(false);
  const open2 = () => setIsDone(true);

  return { isDone, close2, open2 };
};

export default useDone;
