import { useState } from "react";

// Centralizes modal control
const useDone = () => {
  const [isDone, setIsDone] = useState(false);

  const close = () => setIsDone(false);
  const open = () => setIsDone(true);

  return { isDone, close, open };
};

export default useDone;
