import { motion } from "framer-motion";
import Backdrop from "./backdrop";
import useForm from "./hooks/useForm";

const dropIn = {
  hidden: {
    y: "-100vh",
    opacity: 0,
  },
  visible: {
    y: "0",
    opacity: 1,
    transition: {
      duration: 0.1,
      type: "spring",
      damping: 25,
      stiffness: 500,
    },
  },
  exit: {
    y: "100vh",
    opacity: 0,
  },
};

const dropOut = {
  visible: {
    y: "0",
    opacity: 1,
    transition: {
      duration: 0.1,
      type: "spring",
      damping: 25,
      stiffness: 500,
    },
  },
  hidden: {
    y: "-100vh",
    opacity: 0,
  },
};

const Modal = ({ handleClose }) => {
  const { formOpen, close, open } = useForm();

  console.log(formOpen);

  return (
    <Backdrop onClick={handleClose}>
      <motion.div
        onClick={(e) => e.stopPropagation()}
        className="modal orange-gradient"
        variants={dropIn}
        initial="hidden"
        animate="visible"
        exit="exit"
      >
        <motion.form
          action=""
          method="post"
          onClick={(e) => e.stopPropagation()}
          className="modalform orange-gradient"
          variants={dropOut}
          initial="visible"
          animate="hidden"
          exit="exit"
        >
          <tr>
            <th>Username</th>
            <td>
              <input name="username" type="text" className="form-control" />
            </td>
          </tr>
          <tr>
            <th>Password</th>
            <td>
              <input name="password" type="text" className="form-control" />
            </td>
          </tr>
          <input type="submit" />
        </motion.form>

        <motion.button
          whileHover={{ scale: 1.3 }}
          whileTap={{ scale: 0.9 }}
          className="save-button"
          onClick={() => (formOpen ? close() : open())}
        >
          Test
        </motion.button>
      </motion.div>
    </Backdrop>
  );
};

export default Modal;
