import React, { useState, useEffect, useContext } from "react";
import { Helmet } from "react-helmet";
import { Link } from "react-router-dom";
import { Header } from "./components/header";
import AuthContext from "../utils/AuthContext";
import { motion, AnimatePresence } from "framer-motion";
import { Modal4 } from "./components/modal";
import useModal from "./components/hooks/useModal";

const Trueitems = () => {
  let [notes, setNotes] = useState([]);
  let { authTokens, logoutUser } = useContext(AuthContext);
  var valuex2 = sessionStorage.getItem("valuex2");

  useEffect(() => {
    getNotes();
  }, []);

  let getNotes = async () => {
    let response = await fetch(`/api/orderid/items/${valuex2}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
    });
    let data = await response.json();

    if (response.status === 200) {
      setNotes(data);
    } else if (response.statusText === "Unauthorized") {
      logoutUser();
    }
  };

  return (
    <div>
      <div>
        <div>
          <table className="table-atas">
            <tr>
              <th>Items</th>
            </tr>
          </table>
          <table className="table-atas">
            <tr>
              <th>Product Name</th>
              <th>Product Price</th>
              <th>Quantity</th>
            </tr>
          </table>
        </div>
        {notes.map((user) => (
          <table id="table-bawah" key={user[0]}>
            <tr>
              <td>
                {user[0]} {user[1]}
              </td>
              <td>{user[2]}</td>
              <td>{user[3]}</td>
            </tr>
          </table>
        ))}
      </div>
    </div>
  );
};

const Items = () => {
  let [notes, setNotes] = useState([]);
  let { authTokens, logoutUser } = useContext(AuthContext);
  var valuex2 = sessionStorage.getItem("valuex2");

  useEffect(() => {
    getNotes();
  }, []);

  let getNotes = async () => {
    let response = await fetch(`/api/orderid/${valuex2}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      },
    });
    let data = await response.json();

    console.log(data);

    if (response.status === 200) {
      setNotes(data);
    } else if (response.statusText === "Unauthorized") {
      logoutUser();
    }
  };

  let [quantity, setQuantity] = useState([]);
  let [showButton, setShowButton] = useState(false);

  let { modalOpen, close, open } = useModal();

  return (
    <div>
      {notes.map((user) => (
        <section className="container py-5">
          <div className="row text-center pt-5 pb-3">
